class EllysPairs {
public:int getDifference(vector <int> knowledge) {	
		int ans=0;
		int N=knowledge.size();
		sort(knowledge.begin(),knowledge.end());
		vector<int> v;
		for(int i=0;i*2<N;i++)
			v.push_back(knowledge[i]+knowledge[N-i-1]);

		sort(v.begin(),v.end());
		ans=v[v.size()-1]-v[0];
		return ans;		
	}
};



