class FleasFleas {
public:int population(int n, int k) {	
		long long m=n;
		while(k>0){
			n*=k;
			m+=n;			
			k-=5;
			//cout<<m<<" "<<n<<" "<<k<<endl;
			if(m>10000000)return -1;						
		}
		return m;		
	}

};



