using namespace std; 
typedef long long ll;

class NinjaTurtles {
public:
	int countOpponents(int P, int K) {
		ll low=1,hih=1LL<<35;
		while(low+1<hih){
			ll mid=(low+hih)/2;
			ll sum=(3*(mid/K))+(mid/3);
			if(sum<P)
				low=mid;
			else 
				hih=mid;
		}
		if((3*(hih/K)) + (hih/3)==P)return (int)hih;
		return -1;
	}
};



