typedef long long ll;

class ShuffleSort {
public:
	double shuffle(vector <int> cards) {
		double ans=1;
		int N=cards.size();
		for(int i=0;i+1<N;i++){
			if(cards[i]==cards[i+1])continue;
			if(cards[i]<cards[i+1]){
				i++;
				ans*=2;
			}
			if(cards[i]>cards[i+1]){
				ans*=2;
			}
		}
		return ans;
	}
};
