class SlimeXSlimeRancher2 {
public:int train(vector <int> v) {	
		int ans=0;
		int N=v.size();
		int maxi=0;
		for(int i=0;i<N;i++)maxi=max(maxi,v[i]);
		for(int i=0;i<N;i++)ans+=abs(v[i]-maxi);		
		return ans;		
	}
};
